(()=>{document.addEventListener("shopify:section:load",()=>{e()});function e(){Object.values(document.getElementsByClassName("owl-carousel")).forEach(t=>$(t).owlCarousel({loop:!0,margin:parseInt(t.dataset.spacing),autoplay:!0,autoplayTimeout:1e3,autoplayHoverPause:!0,responsive:{0:{items:parseInt(t.dataset.numofitemmobile)},768:{items:parseInt(t.dataset.numofitem768)},992:{items:parseInt(t.dataset.numofitem992)},1280:{items:parseInt(t.dataset.numofitemdesktop)}}}))}window.onload=e();})();
